mapper.select:
select id, vin_code, v_code, edr_mlog_sn, edr_wifi_module_sn, arcos_sn, car_owner, edr_type, arcos_file_path, edr_file_path, arcos_transmission_path, edr_transmission_path, arcos_backup_path, edr_backup_path, pic_name, available from vehicle where 1 = 1
and vin_code = #{vinCode}
and v_code = #{vCode}
and available = #{available}
order by vin_code

mapper.insert:
insert into vehicle (
id, vin_code, v_code, edr_mlog_sn, edr_wifi_module_sn, arcos_sn, car_owner, edr_type, arcos_file_path, edr_file_path, arcos_transmission_path, edr_transmission_path, arcos_backup_path, edr_backup_path, pic_name, available,
)values (
#{id}, #{vinCode}, #{vCode}, #{edrMlogSn}, #{edrWifiModuleSn}, #{arcosSn}, #{carOwner}, #{edrType}, #{arcosFilePath}, #{edrFilePath}, #{arcosTransmissionPath}, #{edrTransmissionPath}, #{arcosBackupPath}, #{edrBackupPath}, #{picName}, #{available},
)
